mapper.select:
SELECT MOLMI.LAYOUT_ID, MOLMI.OPRTR_ID, MOLMI.MENU_ID, MOLMI.LAYOUT_MENU_NM, MOLMI.LAYOUT_STTS_CD, MOLMI.LAYOUT_1_USE_YN, MOLMI.LAYOUT_2_USE_YN, MOLMI.LAYOUT_3_USE_YN, MOLMI.FNCT_TYPE, MOLMI.DATA_TYPE_CD, MOM.MENU_NM, MOM.MENU_PTTRN_TYPE FROM M_OP_LAYOUT_MST_INFO MOLMI LEFT JOIN M_OP_MENU MOM ON MOM.MENU_ID = MOLMI.MENU_ID WHERE 1=1
AND MOLMI.OPRTR_ID = #{oprtrId}
AND MOLMI.MENU_ID IN
#{menuId}
AND MOLMI.DATA_TYPE_CD = #{dataTypeCd}
ORDER BY MOLMI.DATA_TYPE_CD,MOLMI.MENU_ID ,MOLMI.LAYOUT_MENU_NM ASC

mapper.update:
UPDATE M_OP_LAYOUT_MST_INFO SET LAYOUT_MENU_NM = #{layoutMenuNm}, LAYOUT_STTS_CD = #{layoutSttsCd}, LAYOUT_1_USE_YN = #{layout1UseYn}, LAYOUT_2_USE_YN = #{layout2UseYn}, LAYOUT_3_USE_YN = #{layout3UseYn}, FNCT_TYPE = #{fnctType}, DATA_TYPE_CD = #{dataTypeCd} WHERE 1=1
AND LAYOUT_ID = #{layoutId}
AND OPRTR_ID = #{oprtrId}
