mapper.select:
SELECT COALESCE(TRAFFIC_CNT_INFO.TODAY_TRFVLM,0) AS "averageCnt", COALESCE(ROUND((TRAFFIC_CNT_INFO.TODAY_TRFVLM - TRAFFIC_CNT_INFO.YESTERDAY_TRFVLM)/TRAFFIC_CNT_INFO.YESTERDAY_TRFVLM*100,0),0) AS "compareCnt" FROM ( SELECT COALESCE(SUM(TRFVLM),0) AS TODAY_TRFVLM , ( SELECT SUM(TRFVLM) FROM MRT_STD_LINK_SECTN_INFO WHERE ANLS_DT >= TO_TIMESTAMP(#{beforeTime},'YYYY-MM-DD HH24')+ INTERVAL '-1 days' AND ANLS_DT <= TO_TIMESTAMP(#{currentTime},'YYYY-MM-DD HH24')+ INTERVAL '-1 days'
) AS YESTERDAY_TRFVLM FROM
MRT_STD_LINK_SECTN_INFO WHERE ANLS_DT >= TO_TIMESTAMP(#{beforeTime},'YYYY-MM-DD HH24') AND ANLS_DT <= TO_TIMESTAMP(#{currentTime},'YYYY-MM-DD HH24')
) TRAFFIC_CNT_INFO

WITH TRAFFIC_GRAPH_DATA AS ( SELECT TO_CHAR(TO_TIMESTAMP(TIME_DATA.GRAPH_TIME,'YYYY-MM-DD HH24'),'HH24'||'ì‹œ') AS TIME_DATA, COALESCE(SUM(GRAPH_DATA.TRFVLM),0) AS CNT_DATA FROM ( SELECT TO_CHAR( GENERATE_SERIES(TO_TIMESTAMP(#{currentTime},'YYYY-MM-DD HH24')+ INTERVAL '-5 hours', TO_TIMESTAMP(#{currentTime},'YYYY-MM-DD HH24')+ INTERVAL '-1 hours', '1 hours') ,'YYYY-MM-DD HH24') AS GRAPH_TIME
) TIME_DATA LEFT JOIN ( SELECT MSLSI.TRFVLM, TO_CHAR(MSLSI.ANLS_DT,'YYYY-MM-DD HH24') AS CLCT_TIME FROM MRT_STD_LINK_SECTN_INFO MSLSI WHERE MSLSI.ANLS_DT >= TO_TIMESTAMP(#{currentTime},'YYYY-MM-DD HH24')+ INTERVAL '-6 hours' AND MSLSI.ANLS_DT < TO_TIMESTAMP(#{currentTime},'YYYY-MM-DD HH24')
)GRAPH_DATA ON GRAPH_DATA.CLCT_TIME = TIME_DATA.GRAPH_TIME GROUP BY TIME_DATA.GRAPH_TIME ) SELECT ARRAY_TO_STRING(ARRAY_AGG(TIME_DATA ORDER BY TIME_DATA ASC),',') AS "graphTime", ARRAY_TO_STRING(ARRAY_AGG(CNT_DATA ORDER BY TIME_DATA ASC),',') AS "graphCnt" FROM TRAFFIC_GRAPH_DATA

SELECT COALESCE(ROUND(TRAFFIC_AVG_SPEED_INFO.TODAY_AVG_SPEED,0),0) AS "averageCnt", COALESCE(ROUND((TRAFFIC_AVG_SPEED_INFO.TODAY_AVG_SPEED - TRAFFIC_AVG_SPEED_INFO.YESTERDAY_AVG_SPEED)/TRAFFIC_AVG_SPEED_INFO.YESTERDAY_AVG_SPEED*100,0),0) AS "compareCnt" FROM ( SELECT AVG(SPEED) AS TODAY_AVG_SPEED , ( SELECT AVG(SPEED) AS AVG_SPEED FROM MRT_STD_LINK_SECTN_INFO WHERE ANLS_DT >= TO_TIMESTAMP(#{beforeTime},'YYYY-MM-DD HH24')+ INTERVAL '-1 days' AND ANLS_DT <= TO_TIMESTAMP(#{currentTime},'YYYY-MM-DD HH24')+ INTERVAL '-1 days'
) AS YESTERDAY_AVG_SPEED FROM
MRT_STD_LINK_SECTN_INFO WHERE ANLS_DT >= TO_TIMESTAMP(#{beforeTime},'YYYY-MM-DD HH24') AND ANLS_DT <= TO_TIMESTAMP(#{currentTime},'YYYY-MM-DD HH24')
) TRAFFIC_AVG_SPEED_INFO

WITH TRAFFIC_GRAPH_DATA AS ( SELECT TO_CHAR(TO_TIMESTAMP(TIME_DATA.GRAPH_TIME,'YYYY-MM-DD HH24'),'HH24'||'ì‹œ') AS TIME_DATA, COALESCE(ROUND(AVG(GRAPH_DATA.SPEED),0),0) AS CNT_DATA FROM ( SELECT TO_CHAR( GENERATE_SERIES(TO_TIMESTAMP(#{currentTime},'YYYY-MM-DD HH24')+ INTERVAL '-5 hours', TO_TIMESTAMP(#{currentTime},'YYYY-MM-DD HH24')+ INTERVAL '-1 hours', '1 hours') ,'YYYY-MM-DD HH24') AS GRAPH_TIME
) TIME_DATA LEFT JOIN ( SELECT MSLSI.SPEED, TO_CHAR(MSLSI.ANLS_DT,'YYYY-MM-DD HH24') AS CLCT_TIME FROM MRT_STD_LINK_SECTN_INFO MSLSI WHERE MSLSI.ANLS_DT >= TO_TIMESTAMP(#{currentTime},'YYYY-MM-DD HH24')+ INTERVAL '-6 hours' AND MSLSI.ANLS_DT < TO_TIMESTAMP(#{currentTime},'YYYY-MM-DD HH24')
)GRAPH_DATA ON GRAPH_DATA.CLCT_TIME = TIME_DATA.GRAPH_TIME GROUP BY TIME_DATA.GRAPH_TIME ) SELECT ARRAY_TO_STRING(ARRAY_AGG(TIME_DATA ORDER BY TIME_DATA ASC),',') AS "graphTime", ARRAY_TO_STRING(ARRAY_AGG(CNT_DATA ORDER BY TIME_DATA ASC),',') AS "graphCnt" FROM TRAFFIC_GRAPH_DATA
